CREATE SCHEMA `university` ;
CREATE TABLE `university`.`admins` (`adminId` INT NOT NULL,`firstName` VARCHAR(45) NULL,`lastName` VARCHAR(45) NULL,`email` VARCHAR(45) NULL,`phone` VARCHAR(45) NULL,`address` VARCHAR(45) NULL,`DOB` DATE NULL,`passwords` VARCHAR(45) NULL,PRIMARY KEY (`adminId`));
CREATE TABLE `university`.`librarybooks` (`bookid` INT NOT NULL,`bookName` VARCHAR(45) NULL,`authorName` VARCHAR(45) NULL,`publicationYear` DATE NULL,`availability` INT NULL,PRIMARY KEY (`bookid`));
CREATE TABLE `university`.`students` (`studentId` INT NOT NULL,`studentFirstName` VARCHAR(45) NULL,`studentLastName` VARCHAR(45) NULL,`Sex` VARCHAR(6) NULL,`dateOfBirth` DATE NULL,`Department` VARCHAR(45) NULL,`course` VARCHAR(45) NULL,`contactNumber` VARCHAR(10) NULL,`emailId` VARCHAR(45) NULL,PRIMARY KEY (`studentId`));
CREATE TABLE `university`.`users` (`UserId` INT NOT NULL AUTO_INCREMENT,`UserName` VARCHAR(45) NULL,`UserLastName` VARCHAR(45) NULL,`UserEmail` VARCHAR(45) NULL,`UserPhone` VARCHAR(45) NULL,`UserAddress` VARCHAR(45) NULL,`DateOFBirth` DATE NULL,`Passwords` VARCHAR(45) NULL,PRIMARY KEY (`UserId`));
CREATE TABLE `university`.`fined` (`studentId` INT NOT NULL,`cost` INT NULL,PRIMARY KEY (`studentId`),CONSTRAINT `finedId`FOREIGN KEY (`studentId`) REFERENCES `university`.`students` (`studentId`) ON DELETE NO ACTION ON UPDATE NO ACTION);
CREATE TABLE `university`.`issuebook` (`bookId` INT NOT NULL,`studentId` INT NULL,`date` DATE NULL, PRIMARY KEY (`bookId`), CONSTRAINT `bookId` FOREIGN KEY (`bookId`) REFERENCES `university`.`librarybooks` (`bookId`) ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT `studentId` FOREIGN KEY (`bookId`) REFERENCES `university`.`students` (`studentId`) ON DELETE NO ACTION ON UPDATE NO ACTION);
CREATE TABLE `university`.`transactions` (`studentId` INT NOT NULL,`Fine` INT NULL,`tDate` DATE NULL,`Paid` VARCHAR(45) NULL, PRIMARY KEY (`studentId`), CONSTRAINT `transactionId` FOREIGN KEY (`studentId`) REFERENCES `university`.`students` (`studentId`) ON DELETE NO ACTION ON UPDATE NO ACTION);
